  `inspectResult` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`course_id`),
  KEY `FKAF42E01BD0464679` (`classify_id`),
  KEY `FKAF42E01BF3526499` (`user_id`),
  CONSTRAINT `course_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`),
  CONSTRAINT `course_ibfk_2` FOREIGN KEY (`classify_id`) REFERENCES `classify` (`classify_id`),
  CONSTRAINT `FKAF42E01BD0464679` FOREIGN KEY (`classify_id`) REFERENCES `classify` (`classify_id`),
